<?phpdeclare(strict_types=1);namespace Haralb\FinanceCalculator\Calculator;use Haralb\FinanceCalculator\Enum\Period;use Haralb\FinanceCalculator\ValueObject\Money;final class AprCalculator{    /**     * @param Money $principal     * @param float $aprPercent     * @param int $units     * @param Period $period     * @return Money     * @throws \Exception     */    public function profit(        Money $principal,        float $aprPercent,        int $units,        Period $period    ): Money {        $rate = $aprPercent / 100;        $yearsFraction = $units / $period->value;        $amount = $principal->amount * $rate * $yearsFraction;        return new Money($amount);    }}