<?phpdeclare(strict_types=1);namespace Haralb\FinanceCalculator\Calculator;use Haralb\FinanceCalculator\Enum\Period;final class AprCalculator{    /**     * @param float $principal     * @param float $aprPercent     * @param int $units     * @param Period $period     * @return float     */    public function profit(        float $principal,        float $aprPercent,        int $units,        Period $period    ): float {        $rate = $aprPercent / 100;        $yearsFraction = $units / $period->value;        return $principal * $rate * $yearsFraction;    }}