<?phpdeclare(strict_types=1);namespace Haralb\FinanceCalculator\Calculator;use Haralb\FinanceCalculator\Enum\Period;final class GoalCalculator{    /**     * @param float $start     * @param float $target     * @param float $aprPercent     * @param Period $period     * @return int     */    public function periodsToTarget(        float $start,        float $target,        float $aprPercent,        Period $period    ): int {        $r = $aprPercent / 100;        $n = $period->compoundsPerYear();        $years = log($target / $start) /            ($n * log(1 + $r / $n));        return (int) ceil($years * $n);    }}